package com.mycompany.testverktygbackend.services;import com.mycompany.testverktygbackend.models.Course;import com.mycompany.testverktygbackend.models.Test;import com.mycompany.testverktygbackend.repositories.CourseRepository;import com.mycompany.testverktygbackend.repositories.TestRepository;import java.util.List;public class TestServices {        TestRepository tr = new TestRepository();    public Test addTest(Test test) {       TestRepository db = new TestRepository();       return db.addTest(test);    }    public List<Test> getAllTests(int courseId) {        CourseRepository cR = new CourseRepository();        Course course = cR.getCourse(courseId);                        return course.getTests();    }    Test getTest(int testId) {        return tr.getTest(testId);    }    public void deleteTest(int testId) {        tr.deleteTest(testId);    }    public Test updateTest(Test test, int userId, int courseId) {        CourseService cs = new CourseService();        List<Course> c = cs.getCourses(userId);        for(Course course : c){            if(course.getCourseId() == courseId){                test.setCourse(course);            }        }                return tr.updateTest(test);    }    }